mapper.insert:
insert into order_info (o_id, t_id, t_name, 
  t_info_id, t_info_img, t_info_money, 
  o_time, o_number, o_money, 
  t_info_title, is_evaluation, is_order
  )
values (#{oId,jdbcType=INTEGER}, #{tId,jdbcType=INTEGER}, #{tName,jdbcType=VARCHAR}, 
  #{tInfoId,jdbcType=INTEGER}, #{tInfoImg,jdbcType=VARCHAR}, #{tInfoMoney,jdbcType=DECIMAL}, 
  #{oTime,jdbcType=VARCHAR}, #{oNumber,jdbcType=INTEGER}, #{oMoney,jdbcType=DECIMAL}, 
  #{tInfoTitle,jdbcType=VARCHAR}, #{isEvaluation,jdbcType=VARCHAR}, #{isOrder,jdbcType=VARCHAR}
  )

mapper.select:
	select
	
  from order_info
  
  	
  		t_id = #{tId}
  	
  	
  		and is_evaluation = #{isEvaluation}
  	
  	
  		and is_order = #{isOrder}
  	
  
  order by o_time desc
