mapper.insert:
insert into t_user_info (t_id, t_name, t_img, 
  t_pass, t_email, t_phone, 
  t_question, t_answer, t_address, 
  t_money, t_payword, t_regtime
  )
values (#{tId,jdbcType=INTEGER}, #{tName,jdbcType=VARCHAR}, #{tImg,jdbcType=VARCHAR}, 
  #{tPass,jdbcType=VARCHAR}, #{tEmail,jdbcType=VARCHAR}, #{tPhone,jdbcType=VARCHAR}, 
  #{tQuestion,jdbcType=VARCHAR}, #{tAnswer,jdbcType=VARCHAR}, #{tAddress,jdbcType=VARCHAR}, 
  #{tMoney,jdbcType=DECIMAL}, #{tPayword,jdbcType=VARCHAR}, #{tRegtime,jdbcType=TIMESTAMP}
  )

mapper.update:
	update t_user_info
  set t_money = t_money + #{tMoney,jdbcType=DECIMAL}
  where t_id = #{tId,jdbcType=INTEGER}
